import React, { useEffect, useState } from "react";
import { useNavigate } from 'react-router-dom';
import "./GetSumStocks.css";

const GetSumStocks = () => {
  const [stockSummary, setStockSummary] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [selectedYear, setSelectedYear] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    fetchStockSummary();
  }, []);

  const fetchStockSummary = async () => {
    setLoading(true);
    setError(null);
    try {
      const response = await fetch("http://localhost:8080/stocks/summary");
      if (!response.ok) {
        throw new Error(`Hiba a lekérdezés során: ${response.status}`);
      }
      const data = await response.json();
      setStockSummary(data);
    } catch (err) {
      console.error("Hiba:", err);
      setError("Nem sikerült lekérni az adatokat.");
    } finally {
      setLoading(false);
    }
  };

  const handleYearClick = async (year) => {
    setSelectedYear(year);
    setLoading(true);
    setError(null);

    try {
      let url = "http://localhost:8080/stocks/summary";
      if (year !== null) {
        url = `http://localhost:8080/stocks/by-purchase-year?year=${year}`;
      }

      const response = await fetch(url);
      if (!response.ok) throw new Error("Hiba az adatok lekérésekor.");
      const data = await response.json();
      setStockSummary(data);
    } catch (err) {
      console.error("Hiba:", err);
      setError("Nem sikerült lekérni az év szerinti adatokat.");
    } finally {
      setLoading(false);
    }
  };

  if (loading) return <p className="loading-msg">Betöltés folyamatban...</p>;
  if (error) return <p className="error-msg">{error}</p>;

  return (
    <div className="stock-summary-container">
      <button onClick={() => navigate('/stocks')} className="back-button">
        ← Vissza a részvényeimhez
      </button>

      <div className="year-button-group">
        <button className={`year-button ${selectedYear === null ? "active" : ""}`} onClick={() => handleYearClick(null)}>
          Összes
        </button>
        {[2023, 2022, 2021].map((year) => (
          <button key={year} className={`year-button ${selectedYear === year ? "active" : ""}`} onClick={() => handleYearClick(year)}>
            {year}
          </button>
        ))}
      </div>

      <h2 className="stock-summary-title">Részvényeim összesítve</h2>

      <table className="stock-summary-table">
        <thead>
          <tr>
            <th>Név</th>
            <th>Azonosító</th>
            <th>Mennyiség</th>
            <th>Rövid leírás</th>
          </tr>
        </thead>
        <tbody>
          {stockSummary.map((stock, index) => (
            <tr key={index}>
              <td>{stock.name}</td>
              <td>{stock.stockIdentifier}</td>
              <td>{stock.amount}</td>
              <td>{stock.description}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default GetSumStocks;
