import React, { useEffect, useState } from 'react';
import "./GetAllStocks.css";
import { useNavigate } from 'react-router-dom';

const GetAllStocks = () => {
    const [stocks, setStocks] = useState([]); // State to store stocks
    const [page, setPage] = useState(0); // State to store the current page number
    const [totalPages, setTotalPages] = useState(1); // State to store total number of pages
    const [loading, setLoading] = useState(true); // State to handle loading state
    const [error, setError] = useState(null); // State to handle error message
    const [searchTerm, setSearchTerm] = useState(''); // State to handle search input
    const [searchMode, setSearchMode] = useState(false); // State to toggle search mode

    // New states for editing
    const [isEditing, setIsEditing] = useState(false); // Flag to track if we're editing
    const [editingStock, setEditingStock] = useState(null); // Stock data being edited

    const navigate = useNavigate();

    // Fetch stocks when page or searchMode changes
    useEffect(() => {
        if (!searchMode) {
            fetchStocks(page);
        }
    }, [page, searchMode]);

    // Function to fetch stocks with pagination
    const fetchStocks = async (pageNumber) => {
        setLoading(true);
        setError(null);
        try {
            const response = await fetch(`http://localhost:8080/stocks?page=${pageNumber}&size=10&sort=name`);
            if (!response.ok) {
                throw new Error(`Error: ${response.status}`);
            }
            const data = await response.json();
            setStocks(data.content);
            setTotalPages(data.totalPages);
        } catch (err) {
            setError(err.message);
            setStocks([]);
            setTotalPages(1);
        } finally {
            setLoading(false);
        }
    };

    // Function to handle stock update
    const handleUpdateStock = async (stock) => {
        setIsEditing(true);
        setEditingStock(stock); // Set the stock to be edited
    };

    // Handle form submission for editing stock
    const handleEditSubmit = async (event) => {
        event.preventDefault();

        const updatedStock = {
            ...editingStock, // Spread the current data
            name: event.target.name.value,
            amount: event.target.amount.value,
            description: event.target.description.value
        };

        try {
            const response = await fetch(`http://localhost:8080/stocks/${editingStock.id}`, {
                method: 'PUT',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(updatedStock),
            });

            if (!response.ok) {
                throw new Error(`Error updating stock: ${response.status}`);
            }

            // Refresh the list after successful update
            if (!searchMode) {
                fetchStocks(page);
            } else {
                fetchFilteredStocks();
            }

            // Exit the editing mode
            setIsEditing(false);
            setEditingStock(null);
        } catch (error) {
            setError(error.message);
        }
    };

    // Function to fetch filtered stocks based on search term
    const fetchFilteredStocks = async () => {
        setLoading(true);
        setError(null);
        setSearchMode(true);
        try {
            const response = await fetch(`http://localhost:8080/stocks/filter?filter=${searchTerm}`);
            if (!response.ok) {
                throw new Error(`Error: ${response.status}`);
            }
            const data = await response.json();
            setStocks(data);
            setTotalPages(1);
            setPage(0);
        } catch (err) {
            setError(err.message);
            setStocks([]);
        } finally {
            setLoading(false);
        }
    };

    // Function to clear search and reset the state to default
    const handleClearSearch = () => {
        setSearchTerm('');
        setSearchMode(false);
        fetchStocks(0);
    };

    // Navigate to stock summary page
    const handleSummaryClick = () => {
        navigate('/stocks/summary');
    };

    return (
        <div className="stock-list-container">
            <div className="stock-list-header">
                <div className="header-side">
                    <input
                        type="text"
                        placeholder="Search by name, identifier, or description..."
                        value={searchTerm}
                        onChange={(e) => setSearchTerm(e.target.value)}
                        className="stock-search-input"
                    />
                    <button onClick={fetchFilteredStocks} className="stock-search-button">Search</button>
                    {searchMode && (
                        <button onClick={handleClearSearch} className="stock-search-clear">X</button>
                    )}
                </div>

                <h2 className="stock-list-title">My Stocks</h2>
                <button className="stock-summary-button" onClick={handleSummaryClick}>
                    Stock Summary
                </button>
            </div>

            {loading && <p className="loading-error">Loading...</p>}
            {error && <p className="loading-error">Error: {error}</p>}

            {/* Show edit form if we are in editing mode */}
            {isEditing && (
                <form onSubmit={handleEditSubmit} className="edit-stock-form">
                    <input
                        type="text"
                        name="name"
                        defaultValue={editingStock.name}
                        required
                        placeholder="Stock Name"
                    />
                    <input
                        type="number"
                        name="amount"
                        defaultValue={editingStock.amount}
                        required
                        placeholder="Amount"
                    />
                    <input
                        type="text"
                        name="description"
                        defaultValue={editingStock.description}
                        required
                        placeholder="Description"
                    />
                    <button type="submit">Save</button>
                </form>
            )}

            {/* Show stocks list if not in editing mode */}
            {!isEditing && !loading && !error && (
                <>
                    <ul className="stock-list">
                        {stocks.map(stock => (
                            <li key={stock.id} className="stock-item">
                                <div className="stock-details">
                                    <p>Name: <span>{stock.name}</span></p>
                                    <p>Amount: <span>{stock.amount}</span></p>
                                    <p>Description: <span>{stock.description}</span></p>

                                    {/* Modify and Delete buttons */}
                                    <button
                                        className="delete-stock-button"
                                        onClick={() => handleDeleteStock(stock.id)}
                                    >
                                        X
                                    </button>
                                    <button
                                        className="modify-stock-button"
                                        onClick={() => handleUpdateStock(stock)}
                                    >
                                        Modify
                                    </button>
                                </div>
                            </li>
                        ))}
                    </ul>

                    <div className="pagination-container">
                        <button
                            onClick={() => setPage((prev) => Math.max(prev - 1, 0))}
                            disabled={page === 0}
                            className="pagination-button"
                        >
                            Previous
                        </button>
                        <span className="pagination-info">{page + 1} / {totalPages}</span>
                        <button
                            onClick={() => setPage((prev) => Math.min(prev + 1, totalPages - 1))}
                            disabled={page === totalPages - 1}
                            className="pagination-button"
                        >
                            Next
                        </button>
                    </div>
                </>
            )}
        </div>
    );
};

export default GetAllStocks;
